import React from "react";
import { useWindowSize } from "../../utils/useWindowSize";

function Orb() {
  const { width, height } = useWindowSize();

  // Defining keyframes for orb animation
  const moveOrb = {
    animation: `orbAnimation 1s alternate linear infinite`,
  };

  return (
    <div
      className="absolute rounded-full bg-gradient-to-b from-pink-400 to-orange-400 blur-[400px]"
      style={{
        width: "70vh",
        height: "70vh",
        marginLeft: "-35vh",
        marginTop: "-35vh",
        ...moveOrb,
      }}
    ></div>
  );
}

export default Orb;
