import React from "react";
import { dateFormat } from "../../utils/dateFormat";
import {
  bitcoin,
  book,
  calender,
  card,
  circle,
  clothing,
  comment,
  rupee,
  food,
  freelance,
  medical,
  money,
  piggy,
  stocks,
  takeaway,
  trash,
  tv,
  users,
  yt,
} from "../../utils/Icons";
import Button from "../Button/Button";

function IncomeItem({
  id,
  title,
  amount,
  date,
  category,
  description,
  deleteItem,
  indicatorColor,
  type,
}) {
  const categoryIcon = () => {
    switch (category) {
      case "salary":
        return money;
      case "freelancing":
        return freelance;
      case "investments":
        return stocks;
      case "stocks":
        return users;
      case "bitcoin":
        return bitcoin;
      case "bank":
        return card;
      case "youtube":
        return yt;
      case "other":
        return piggy;
      default:
        return "";
    }
  };

  const expenseCatIcon = () => {
    switch (category) {
      case "education":
        return book;
      case "groceries":
        return food;
      case "health":
        return medical;
      case "subscriptions":
        return tv;
      case "takeaways":
        return takeaway;
      case "clothing":
        return clothing;
      case "travelling":
        return freelance;
      case "other":
        return circle;
      default:
        return "";
    }
  };

  return (
    <div className="bg-white border-[1px] border-black  rounded-xl p-4 mb-4 flex items-center gap-4 w-full text-gray-800">
      <div className=" p-2 rounded-lg bg-white flex items-center justify-center ">
        {type === "expense" ? expenseCatIcon() : categoryIcon()}
      </div>
      <div className="flex-1 flex flex-col gap-1">
        <h5 className="text-lg relative ">{title}</h5>
        <div className="flex justify-between items-center">
          <div className="flex items-center gap-6 text-[#73D2DE] opacity-80">
            <p className="flex items-center gap-2">
              {rupee} {amount}
            </p>
            <p className="flex items-center gap-2">
              {calender} {dateFormat(date)}
            </p>
            <p className="flex items-center gap-2">
              {comment} {description}
            </p>
          </div>
          <div className="btn-con">
            <Button
              icon={trash}
              bPad={"1rem"}
              bRad={"50%"}
              bg={"var(--primary-color"}
              color={"#fff"}
              iColor={"#fff"}
              hColor={"var(--color-green)"}
              onClick={() => deleteItem(id)}
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default IncomeItem;
